// src/components/Page1.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import firebase from 'firebase/compat/app';
import 'firebase/compat/storage';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const Page1 = () => {

    const [image, setImage] = useState(null);

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(file);
        }
    };
    const [formData, setFormData] = useState({
        name: '',
        description: '',
        quantity: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const storageRef = firebase.storage().ref();
            const fileRef = storageRef.child(image.name);
            await fileRef.put(image);
            const url = await fileRef.getDownloadURL();
            console.log('Download URL:', url);

            const { name, description, quantity } = formData;
            const response = await fetch('https://localhost:7050/api/Prize', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id: 0,
                    prizeName: name,
                    imageLink: url,
                    quantity: quantity,
                    prizeDescription: description,
                    members: []
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to submit form');
            } setFormData({
                name: '',
                description: '',
                quantity: ''
            });

            // Hiển thị thông báo
            toast("Thêm giải thưởng thành công");

            console.log('Server response:', response);
        } catch (error) {
            console.error('Error submitting form:', error.message);
        }
    };

    return (
        <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center', justifyContent: 'center', minHeight: '100vh' }}>
            <ToastContainer />
            <Header />
            <main>
                <h2>This is Page 1</h2>
            </main>
            <form onSubmit={handleSubmit} style={{ textAlign: 'center', width: '300px' }}>
                <h1>This is the InfoForm Page</h1>
                <p>Welcome to the InfoForm Page!</p>
                <label>
                    Tên giải:
                    <input
                        type="text"
                        name="name"
                        value={formData.name}
                        onChange={handleChange}
                        autoComplete="new-password"
                    />
                </label>
                <br />
                <label>
                    Mô tả:
                    <input
                        type="text"
                        name="description"
                        value={formData.description}
                        onChange={handleChange}
                        autoComplete="new-password"
                    />
                </label>
                <br />
                <label>
                    Số lượng giải:
                    <input
                        type="number"
                        name="quantity"
                        value={formData.quantity}
                        onChange={handleChange}
                        autoComplete="new-password"
                    />
                </label>
                <br />
                <button type="submit">Submit</button>
            </form>
            <input type="file" onChange={handleFileChange} />
        </div>

    );
};

export default Page1;
