import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useParams } from 'react-router-dom';
import Header from './Header';
import './css/Page4.css';
const Page4 = () => {
    const { id } = useParams();
    const navigate = useNavigate();
    const [data, setData] = useState(null);

    useEffect(() => {
        // Gọi API ở đây và cập nhật state "data"
        // Ví dụ:
        const fetchData = async () => {
            try {
                const response = await fetch('https://lotteryapi20231223201647.azurewebsites.net//api/Prize/' + id);
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const result = await response.json();
                setData(result);
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        fetchData();
    }, null); // Rỗng để đảm bảo chỉ gọi API một lần khi component được mount
    if (!data) {
        return <p>Loading...</p>;
    }

    if (data.quantity === 1)
        return (
            <div className='bodyPage4'>
                <Header />
                <div className='mainPage4'>
                    <h2 className='h2Prize1'>{data.prizeName}</h2>
                    <h3>{data.prizeDescription}</h3>
                    <img className='imgPrize' src={data.imageLink} />
                    <li>
                        <h1 className='prize1'>{data.members[0].memberName}</h1>
                        <h1 className='prize1Phongban'>{data.members[0].memberName}</h1>
                    </li>

                </div>

            </div>
        );
    if (data.quantity === 3)
        return (
            <div className='bodyPage4'>
                <Header />
                <div className='mainPage4'>
                    <h2>{data.prizeName}</h2>
                    <h3>{data.prizeDescription}</h3>
                    <img className='imgPrize' src={data.imageLink} />
                    <div className='listWin2'>
                        <ul>
                            {data.members.map((item) => (
                                <li key={item.id}>
                                    <h4 className='namePage4'>{item.memberName}</h4>
                                    <p className='phonePage4'>{item.phoneNumber}</p>
                                </li>
                            ))}
                        </ul>
                    </div>

                </div>

            </div>
        );
};

export default Page4;
