import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Page3 = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    const [list, setList] = useState([]);
    const [iteration, setIteration] = useState(0);

    const [loaded, setLoaded] = useState(false); // Biến đánh dấu đã tải dữ liệu hay chưa

    useEffect(() => {
        // Chỉ tải dữ liệu nếu loaded là false
        if (!loaded) {
            const fetchData = async () => {
                try {
                    const response = await fetch('https://localhost:7050/api/Member');
                    if (!response.ok) {
                        throw new Error('Failed to fetch data');
                    }
                    const result = await response.json();
                    setData(result);
                    setLoaded(true); // Đặt loaded thành true sau khi tải dữ liệu

                } catch (error) {
                    console.error('Error fetching data:', error.message);
                }
            }

            fetchData();
        }
    }, [loaded]);
    useEffect(() => {
        // Xử lý delay và hiển thị id
        const delayedDisplay = () => {
            data.forEach((item, index) => {
                setTimeout(() => {
                    setList((prevList) => [...prevList, item.id]);
                }, index * 1000); // Mỗi id sẽ hiển thị sau 1 giây
            });
        };

        delayedDisplay();
    }, [data]);


    return (
        <div>
            <p>
                {list.map((id, index) => (
                    <span key={index}>
                        {index < list.length - 1 && ', '}
                    </span>
                ))}
            </p>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        <h4>{item.id}</h4>
                        <p>{item.memberName}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};


export default Page3;
