import React, { useState, useEffect } from 'react';

const Page2 = () => {
    const [data, setData] = useState([]);

    useEffect(() => {
        // Gọi API ở đây và cập nhật state "data"
        // Ví dụ:
        const fetchData = async () => {
            try {
                const response = await fetch('https://localhost:7050/api/Prize');
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const result = await response.json();
                setData(result);
            } catch (error) {
                console.error('Error fetching data:', error.message);
            }
        };

        fetchData();
    }, []); // Rỗng để đảm bảo chỉ gọi API một lần khi component được mount

    const handleButtonClick = (id) => {
        // Xử lý khi nút được bấm
        console.log(`Button clicked for item with id: ${id}`);
    };

    return (
        <div>
            <h2>This is Page 2</h2>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        {item.prizeName}
                        <button onClick={() => handleButtonClick(item.id)}>Button 1</button>
                        <button onClick={() => handleButtonClick(item.id)}>Button 2</button>
                    </li>
                ))}
            </ul>
        </div>
    );
};

export default Page2;
