import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Page3 = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    const [currentIdIndex, setCurrentIdIndex] = useState(0);
    const [list, setList] = useState([]);
    const [iteration, setIteration] = useState(0);
    const [concatenatedString, setConcatenatedString] = useState('');
    const [availableIndexes, setAvailableIndexes] = useState([]); // Mảng lưu trữ các index có sẵn
    const [currentAvailableIndex, setCurrentAvailableIndex] = useState(0);
    const [loaded, setLoaded] = useState(false); // Biến đánh dấu đã tải dữ liệu hay chưa

    useEffect(() => {
        // Chỉ tải dữ liệu nếu loaded là false
        if (!loaded) {
            const fetchData = async () => {
                try {
                    const response = await fetch('https://localhost:7050/api/Member');
                    if (!response.ok) {
                        throw new Error('Failed to fetch data');
                    }
                    const result = await response.json();
                    setData(result);
                    setAvailableIndexes(Array.from({ length: result.length }, (_, index) => index));
                    setLoaded(true); // Đặt loaded thành true sau khi tải dữ liệu

                } catch (error) {
                    console.error('Error fetching data:', error.message);
                }
            }

            fetchData();
        }
    }, [loaded]);
    useEffect(() => {
        // Tạo interval để chuyển đổi id mỗi giây
        const intervalId = setInterval(() => {
            if (availableIndexes.length > 0) {
                const randomIndex = Math.floor(Math.random() * availableIndexes.length);
                setCurrentIdIndex(availableIndexes[randomIndex]);
                setCurrentAvailableIndex(randomIndex);
            } else {
                setAvailableIndexes(Array.from({ length: data.length }, (_, index) => index));
            }
        }, 100);

        return () => clearInterval(intervalId);
    }, [availableIndexes]);
    const handleConcatenate = () => {
        setConcatenatedString(prevString => prevString + data[currentIdIndex].id.toString());

        // Tạo một bản sao mới của mảng availableIndexes và loại bỏ phần tử tương ứng
        setAvailableIndexes(prevIndexes => {
            const newIndexes = [...prevIndexes];
            newIndexes.splice(currentAvailableIndex, 1);
            return newIndexes;
        });
    };

    return (
        <div>
            <p>{data.length > 0 && data[currentIdIndex].id}</p>
            <h1>{concatenatedString}</h1>
            <button onClick={handleConcatenate}>Concatenate</button>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        <h4>{item.id}</h4>
                        <p>{item.memberName}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};


export default Page3;
