// src/components/Page1.js
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import Header from './Header';
import firebase from 'firebase/compat/app';
import 'firebase/compat/storage';


const Page1 = () => {

    const [image, setImage] = useState(null);
    const [imageUrl, setImageUrl] = useState('');
    const navigate = useNavigate();

    const handleFileChange = (e) => {
        const file = e.target.files[0];
        if (file) {
            setImage(file);
        }
    };

    const handleUpload = async () => {
        if (image) {
            const storageRef = firebase.storage().ref();
            const fileRef = storageRef.child(image.name);
            await fileRef.put(image);
            const url = await fileRef.getDownloadURL();
            setImageUrl(url);
        }
    };

    const [formData, setFormData] = useState({
        name: '',
        phone: '',
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value,
        }));
    };

    const handleSubmit = async (e) => {
        e.preventDefault();

        try {
            const { name, phone } = formData;
            const response = await fetch('https://localhost:7050/api/Member', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({
                    id: 0,
                    memberName: name,
                    phoneNumber: phone,
                    status: true,
                }),
            });

            if (!response.ok) {
                throw new Error('Failed to submit form');
            }
            const responseData = await response.json();
            console.log('Server response:', responseData);
        } catch (error) {
            console.error('Error submitting form:', error.message);
        }
    };

    return (
        <div>
            <Header />
            <main>
                <h2>This is Page 1</h2>
            </main>
            <div style={{ display: 'flex', justifyContent: 'center', alignItems: 'center', height: '100vh' }}>
                <form onSubmit={handleSubmit} style={{ textAlign: 'center' }}>
                    <h1>This is the InfoForm Page</h1>
                    <p>Welcome to the InfoForm Page!</p>
                    <label>
                        Name:
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            autoComplete="new-password"
                        />
                    </label>
                    <br />
                    <label>
                        Phone:
                        <input
                            type="text"
                            name="phone"
                            value={formData.phone}
                            onChange={handleChange}
                            autoComplete="new-password"
                        />
                    </label>
                    <input type="file" onChange={handleFileChange} />
                    <button onClick={handleUpload}>Upload</button>
                    {imageUrl && <img src={imageUrl} alt="Uploaded" style={{ maxWidth: '100%' }} />}
                    <br />
                    <br />
                    <button type="submit">Submit</button>
                </form>
            </div>
        </div>
    );
};

export default Page1;
