import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Page3 = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    const [currentIdIndex, setCurrentIdIndex] = useState(0);
    const [list, setList] = useState([]);
    const [iteration, setIteration] = useState(0);

    const [loaded, setLoaded] = useState(false); // Biến đánh dấu đã tải dữ liệu hay chưa

    useEffect(() => {
        // Chỉ tải dữ liệu nếu loaded là false
        if (!loaded) {
            const fetchData = async () => {
                try {
                    const response = await fetch('https://localhost:7050/api/Member');
                    if (!response.ok) {
                        throw new Error('Failed to fetch data');
                    }
                    const result = await response.json();
                    setData(result);
                    setLoaded(true); // Đặt loaded thành true sau khi tải dữ liệu

                } catch (error) {
                    console.error('Error fetching data:', error.message);
                }
            }

            fetchData();
        }
    }, [loaded]);
    useEffect(() => {
        // Tạo interval để chuyển đổi id mỗi giây
        const intervalId = setInterval(() => {
            setCurrentIdIndex(prevIndex => (prevIndex + 1) % data.length);
        }, 1000);

        // Xóa interval khi component unmount hoặc khi data thay đổi
        return () => clearInterval(intervalId);
    }, [data]);


    return (
        <div>
            <p>{data.length > 0 && data[currentIdIndex].id}</p>
            <ul>
                {data.map((item) => (
                    <li key={item.id}>
                        <h4>{item.id}</h4>
                        <p>{item.memberName}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
};


export default Page3;
