import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Page3 = () => {
    const [data, setData] = useState([]);
    const { id } = useParams();
    const [list, setList] = useState([]);
    const [iteration, setIteration] = useState(0);

    useEffect(() => {
        // Gọi API ở đây và cập nhật state "data"
        // Ví dụ:
        const fetchData = async () => {
            try {
                const response = await fetch('https://localhost:7050/api/Member');
                if (!response.ok) {
                    throw new Error('Failed to fetch data');
                }
                const result = await response.json();
                setData(result);

                const delay = 500; // milliseconds
                const delayedList = data.map((item, index) => ({
                    ...item,
                    delay: index * delay, // Tính toán độ trễ cho mỗi phần tử
                }));
                setList([...delayedList, { isIterationMarker: true }]); // Dùng một đánh dấu để đánh dấu lượt lặp

                // Tăng số lần lặp lại sau mỗi lần hiển thị danh sách
                setIteration((prevIteration) => prevIteration + 1);


            } catch (error) {
                console.error('Error fetching data:', error.message);
            }


        }
        fetchData();
    }, [iteration]);



    return (
        <div>
            {list.map((item, index) => (
                <DelayedItem key={index} name={item.name} delay={item.delay} />
            ))}
        </div>
    );
};

const DelayedItem = ({ item, delay }) => {
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        // Thiết lập hiển thị với độ trễ
        const timeoutId = setTimeout(() => {
            setIsVisible(true);
        }, delay);

        // Xóa timeout khi component unmount
        return () => clearTimeout(timeoutId);
    }, [delay]);

    return item.isIterationMarker ? null : isVisible ? <p>{item.id}</p> : null;
};
export default Page3;
